$(".artist:nth-child(1) .overlay, .artist:nth-child(1) .overlay").mouseenter(function(){
			var tween = TweenMax.to('.artist:nth-child(1) .overlay', 1, {css:{opacity:'0.7'}, ease:Power4.easeOut}); 
            
            var tween2 = TweenMax.to('.artist:nth-child(1) .artist-info', 1, {css:{opacity:'1'}, ease:Power4.easeOut}); 
        });

        $(".artist:nth-child(1) .overlay, .artist:nth-child(1) .overlay").mouseleave(function(){
        	var tween = TweenMax.to('.artist:nth-child(1) .overlay', 1, {css:{opacity: '0'}, ease:Power4.easeOut});
            
            var tween2 = TweenMax.to('.artist:nth-child(1) .artist-info', 1, {css:{opacity:'0'}, ease:Power4.easeOut}); 
        });

