$(".overlay:nth-child(1), .overlay:nth-child(1)").mouseenter(function(){
			var tween = TweenMax.to('.overlay:nth-child(1)', 1, {css:{opacity:'1'}, ease:Power4.easeOut});
        });

        $(".overlay:nth-child(1), .overlay:nth-child(1)").mouseleave(function(){
        	var tween = TweenMax.to('.overlay:nth-child(1)', 1, {css:{opacity: '0'}, ease:Power4.easeOut});
        });

